window.addEventListener("load", function () {
    "use strict";
    $('#scrollTop').click(function () {
        $('html,body').animate({ scrollTop: 0 }, 333);
    });

    // back to Top
    $(window).scroll(function () {
        if ($(this).scrollTop() > 300) {
            $('#scrollTop').fadeIn(222);
        } else {
            $('#scrollTop').stop().fadeOut(222);
        }
    }).scroll();
});